# Send to a random 5 journalists from the journalists array, a tweet
def tweet_a_journalist(list)
  journalists_sample = list.sample(5)
  journalists_sample.each do |i|
    login_twitter.update ("#{i} Merci pour le travail remarquable que vous faites. Vive la liberté de la presse! #bonjour_monde @the_hacking_pro")
  end
end


# Like 30 tweets with the hashtag #bonjour_monde
def like_tweets_with_a_given_hashtag
  login_twitter.search('#bonjour_monde').take(30).each do |tweet|
    login_twitter.favorite(tweet)
  end
end

